import React from "react";
import PrimarySearchAppBar from "../component/Navbar";
import { Box, Typography } from "@mui/material";
import styled from "styled-components";
import homeImg from "../image/7351141.jpg"; // Ensure this path is correct

// First Section
const SectionFirst = styled(Box)`
  position: relative;
  top: 73px;
`;

const BoxFirst = styled(Box)`
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  margin-top: 12px;
  border-bottom: 1px solid #8080807d;
`;

const HomeSection = styled(Box)`
  margin-top: 12px;
  height: 100vh; /* Full viewport height */
`;

const HomeImageSection = styled(Box)`
  position: relative;
  height: 472px; /* Define height for the section */
  width: 100%; /* Ensure full width */

  /* Debugging: Add a visible background color to the section to verify it's rendered */
  background-color: lightblue; 

  ::before {
    content: " ";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%; /* Take the full width */
    height: 100%; /* Take the full height of the parent */
    background-image: url(${homeImg}); /* Background image from import */
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover; /* Use cover for better scaling */
    border: 2px solid red; /* Border for debugging */
    z-index: -1; /* Ensure it stays behind the content */
  }
`;

export const HomeScreen = () => {
  return (
    <>
      <PrimarySearchAppBar />
      <SectionFirst>
        <BoxFirst>
          <Box>
            <Typography variant="subtitle1">Mobile</Typography>
          </Box>
          <Box>
            <Typography variant="subtitle1">Electronics</Typography>
          </Box>
          <Box>
            <Typography variant="subtitle1">Fashion</Typography>
          </Box>
          <Box>
            <Typography variant="subtitle1">Furniture</Typography>
          </Box>
        </BoxFirst>
      </SectionFirst>
      <HomeSection>
        <HomeImageSection />
      </HomeSection>
    </>
  );
};
