import React from "react";
import PrimarySearchAppBar from "../component/Navbar";
import { Box, Typography } from "@mui/material";
import styled from "styled-components";
import homeImg from "../image/7351141.jpg"; // Ensure this path is correct

const SectionFirst = styled(Box)`
  position: relative;
  top: 73px;
`;

const BoxFirst = styled(Box)`
  display: flex;
  justify-content: space-evenly;
  align-items: center;
  margin-top: 12px;
  border-bottom: 1px solid #8080807d;
`;

const HomeSection = styled(Box)`
  margin-top: 12px;
  height: 100vh; /* Ensures the full viewport height */
`;

const HomeImageSection = styled(Box)`
  position: relative;
  height: 472px; /* Set the height of the container so the pseudo-element has space */
  width: 100%; /* Ensure it takes the full width */

  ::before {
    content: " ";
    position: absolute;
    top: 0; /* Ensure it starts from the top */
    left: 0; /* Ensure it starts from the left */
    width: 100%; /* Fill the full width */
    height: 472px; /* Same height as the parent container */
    ::before {
  background-image: url('https://via.placeholder.com/600x400'); /* Test with a placeholder image */
}

    background-position: center;
    background-repeat: no-repeat;
    background-size: contain;
    z-index: -1; /* Ensure it's behind other content */
  }
`;

export const HomeScreen = () => {
  return (
    <>
      <PrimarySearchAppBar />
      <SectionFirst>
        <BoxFirst>
          <Box>
            <Typography variant="subtitle1">Mobile</Typography>
          </Box>
          <Box>
            <Typography variant="subtitle1">Electronics</Typography>
          </Box>
          <Box>
            <Typography variant="subtitle1">Fashion</Typography>
          </Box>
          <Box>
            <Typography variant="subtitle1">Furniture</Typography>
          </Box>
        </BoxFirst>
      </SectionFirst>
      <HomeSection>
        <HomeImageSection />
      </HomeSection>
    </>
  );
};
